import './App.css';
import { useState } from 'react';
import TodoListItem from './TodoListItem';

var count = 1
function App() 
{
  const [todo, setTodo] = useState([])
  const [editingFlag, setEditingFlag] = useState(-1)
  const addTodo = () =>
  {
    console.log("addTodo")
    var todoInput = document.getElementById("todoInput").value
    console.log("Todo text :" + todoInput)
    console.log("Todo Array :" + todo)

    const todoTempObject =
    {
      id: count,
      text: todoInput,
      compeleted: true
    }
    count++
    todo.push(todoTempObject)
    setTodo([...todo])
    console.log(todo)
    document.getElementById("todoInput").value = ""
  }

  const deleteTodo = (tempId) =>
 {
    console.log("deleteTodo")
    console.log("tempId :" + tempId)

    var todoUpdate = todo.filter((todoTemp) =>
     {
      if (todoTemp.id === tempId) 
      {
        return false
      }
      return true
    })
    setTodo([...todoUpdate])
  }
  const checkedChange = (todoTemp) =>
 {
    console.log(todoTemp)
    todo.map((element) =>
    {
      if (todoTemp.id === element.id)
      {
        element.compeleted = !element.compeleted
      }
      return element
    })
    setTodo([...todo])
  }


  const editingTodo = (id) =>
  {
    setEditingFlag(id)
  }

 const saveTodo =(obj) =>
 {
   todo.map((element)=>
   {
    if(obj.id === element.id)
    {
      element.text = document.getElementById("editingTodo").value
    }
     return element
   })
   setTodo([...todo])
   setEditingFlag(-1)
  }

 console.log(todo)


 return (

  <div class="body">
    <h3 class="head">To-do App</h3>
    {todo.length >0 ?
    <div>
    <input class="enter"
        id='todoInput'
        type='text'
        placeholder='Enter todo' />
      <button onClick={addTodo} class="addtodo">Add Todo</button>
    </div>
    : <div>
      no todo here<br/>
      <button onClick={addTodo} class="addtodo">Add Todo</button>
      </div>
     }
    {todo.map((todoTemp) => 
      {
        return <TodoListItem
          todoObject={todoTemp} 
          checkedChange={checkedChange}
          deleteTodo={deleteTodo}
          saveTodo={saveTodo}
          editingTodo={editingTodo}
          editingFlag={editingFlag}
          />

      })}
  </div>
);
}

export default App;
