import './App.css';
import { useEffect, useMemo, useState } from 'react';
import TodoListItem from './TodoListItem';
import { Button, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle, TextField } from '@material-ui/core';

var count = 1
var todoCount = 0
function App() 
{
  const [todo, setTodo] = useState([])
  const [editingFlag, setEditingFlag] = useState(-1)
  const [open, setOpen] =useState(false);


  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };
  const openDailog =()=>
  {
    handleClickOpen()
  }
  const INCOMPLETE = "incomplete"
  const COMPLETED ="completed"
  const ALL ="all"
  const [filter,setFilter] =useState(INCOMPLETE)

  const addTodo = () =>
  {
    console.log("addTodo")
    var todoInput = document.getElementById("todoInput").value
    console.log("Todo text :" + todoInput)
    console.log("Todo Array :" + todo)

    const todoTempObject =
    {
      id: count,
      text: todoInput,
      compeleted: false
    }
    count++
    todo.push(todoTempObject)
    setTodo([...todo])
    console.log(todo)
    document.getElementById("todoInput").value = ""
    handleClose()
  }


  const deleteTodo = (tempId) =>
 {
    console.log("deleteTodo")
    console.log("tempId :" + tempId)

    var todoUpdate = todo.filter((todoTemp) =>
     {
      if (todoTemp.id === tempId) 
      {
        return false
      }
      return true
    })
    setTodo([...todoUpdate])
  }
  const checkedChange = (todoTemp) =>
 {
    console.log(todoTemp)
    todo.map((element) =>
    {
      if (todoTemp.id === element.id)
      {
        element.compeleted = !element.compeleted
      }
      return element
    })
    setTodo([...todo])
  }


  const editingTodo = (id) =>
  {
    setEditingFlag(id)
  }

  const saveTodo =(obj) =>
  {
    todo.map((element)=>
    {
     if(obj.id === element.id)
     {
       element.text = document.getElementById("editingTodo").value
     }
      return element
    })
    setTodo([...todo])
    setEditingFlag(-1)
   }

 const handleFilter=(filter)=>
 {
    switch (filter) {
      case INCOMPLETE:
        todoCount=0
        todo.map(todoTemp=>
          {
            if(!todoTemp.compeleted)
            {
              todoCount+=1
            }
          })
        setFilter(INCOMPLETE)
        break;
      case COMPLETED:
        todoCount=0
        todo.map(todoTemp=>
          {
            if(todoTemp.compeleted)
            {
              todoCount+=1
            }
          })
        setFilter(COMPLETED)
        break;
       case ALL:
        todoCount=0
        todoCount = todo.length
        setFilter(ALL)
        break; 
      default:
        break;
    }
 }
 useMemo(()=>
 {
  // todoCount=0
  // todo.map(todoTemp=>
  //   {
  //     if(!todoTemp.compeleted)
  //     {
  //       todoCount+=1
  //     }
  //   })
  handleFilter("incomplete")
 },[])
 const getTodoCount =()=>
 {
  return todoCount
 }

 return (

  <div class="body">
    <h3 class="head">To-do App</h3>
    <div>
    <div style={{justifyContent:"space-evenly"}}>
      Total To-Do count :{getTodoCount()}
    </div>
    <div>
      {filter === INCOMPLETE ?
       <label style={{backgroundColor :"#8EA0AC", margin:"5px"}} onClick={()=>handleFilter("incomplete")}><b>INCOMPLETE</b></label> :
       <label style={{margin:"40px"}} onClick={()=>handleFilter("incomplete")}>INCOMPLETE</label>
      }
      |
      {filter === COMPLETED ?
      <label style={{backgroundColor :"#8EA0AC" ,margin:"5px"}} onClick={()=>handleFilter("completed")}><b>Compeleted</b></label>:
      <label style={{margin:"40px"}} onClick={()=>handleFilter("completed")}>Compeleted</label>
      }
      |
      {filter === ALL ?
      <label style={{backgroundColor :"#8EA0AC", margin:"5px"}} onClick={()=>handleFilter("all")}><b>ALL</b></label>: 
      <label style={{margin:"40px"}} onClick={()=>handleFilter("all")}>ALL</label>
      }
    </div>
    <div>
      {
        todo.length === 0 ?
        <label>No Todo Yet <br/></label>:
        <div></div>
      }
      <div>
      <Dialog
        open={open}
        onClose={handleClose}
         PaperProps={{
           component: 'form',
        //   onSubmit: (event: React.FormEvent<HTMLFormElement>) => {
        //     event.preventDefault();
        //     const formData = new FormData(event.currentTarget);
        //     const formJson = Object.fromEntries((formData as any).entries());
        //     const email = formJson.email;
        //     console.log(email);
            // handleClose();
        //  },
        }}
      >
        <DialogTitle>Add New Todo</DialogTitle>
        <DialogContent>
          {/* <DialogContentText>
            Todo
          </DialogContentText> */}
          <TextField
            autoFocus
            required
            margin="dense"
            id="todoInput"
            name="todo"
            label="Todo"
            type="text"
            fullWidth
            variant="standard"
          />
        </DialogContent>
        <DialogActions>
        <Button onClick={addTodo}>Add Todo</Button>
          <Button onClick={handleClose}>Cancel</Button>
        </DialogActions>
      </Dialog>
      </div>
    {/* <input class="enter"
        id='todoInput'
        type='text'
        placeholder='Enter todo' /> */}
      <button onClick={openDailog} class="addtodo">Add Todo</button>
    </div>
    {/* {todoCount =0} */}
    {todo.map((todoTemp) => 
      {
        let returnComponent

        switch (filter) {
          case INCOMPLETE:
            if(!todoTemp.compeleted)
            {
              returnComponent = <TodoListItem
              key={todoTemp.id}
              todoObject={todoTemp} 
              checkedChange={checkedChange}
              deleteTodo={deleteTodo}
              saveTodo={saveTodo}
              editingTodo={editingTodo}
              editingFlag={editingFlag}
              />
             }
            break;
            case COMPLETED:
            if(todoTemp.compeleted)
            {
              returnComponent = <TodoListItem
              key={todoTemp.id}
              todoObject={todoTemp} 
              checkedChange={checkedChange}
              deleteTodo={deleteTodo}
              saveTodo={saveTodo}
              editingTodo={editingTodo}
              editingFlag={editingFlag}
              />
            }
            break;
            case ALL:
              returnComponent = <TodoListItem
              key={todoTemp.id}
              todoObject={todoTemp} 
              checkedChange={checkedChange}
              deleteTodo={deleteTodo}
              saveTodo={saveTodo}
              editingTodo={editingTodo}
              editingFlag={editingFlag}
              />

             break;
          default:
            break;
        }
        return returnComponent
      })}
    </div>

  </div>
);
}

export default App;
