import './App.css';
const TodoListItem = (props)=>
{
  
    return<div>
    {
      props.todoObject.completed ?
      <input class="checkbox" type='checkbox' onChange={() => props.checkedChange(props.todoObject)} checked /> :
      <input class="checkbox" type='checkbox' onChange={() => props.checkedChange(props.todoObject)}/>
    }
    {
      props.editingFlag !== props.todoObject.id ?
      <>
        {props.todoObject.text}
        <button class="delete" onClick={() => props.deleteTodo(props.todoObject.id)}>Delete </button>
        <button class="edit" onClick={() => props.editingTodo(props.todoObject.id)}>Edit</button>
      </>
      :
      <>
        <input id='editingTodo' type='text' placeholder='Edit todo' Value={props.todoObject.text} />
        <button class="delete" onClick={() => props.deleteTodo(props.todoObject.id)}>Delete</button><delelteIcon/>
        <button class="edit" onClick={() => props.saveTodo(props.todoObject)}>Save Todo</button>
      </>
    }
    </div>
}
export default TodoListItem;
